import brownie
from brownie import Contract
import pytest

def test_swap_10K(deployer, usdc_token, swap_contract, chain, buy_token, allowance_target):
    print("USDC TOKEN BALANCE INITIALLY:", usdc_token.balanceOf(swap_contract.address))
    print("WMATIC TOKEN BALANCE INITIALLY:", buy_token.balanceOf(swap_contract.address))
    swap_contract.fillQuote(usdc_token.address,
                            buy_token.address,
                            allowance_target.address,
                            allowance_target.address,
                            '',
                            {"from":deployer}
    )
    print("USDC TOKEN BALANCE AFTER:", usdc_token.balanceOf(swap_contract.address))
    print("WMATIC TOKEN BALANCE AFTER:", buy_token.balanceOf(swap_contract.address))
